import React, { useEffect } from 'react'
import "./style.css"
import "./mobile.css"
import Navbar from '../common/navbar';
import Footer from '../common/footer';
import { HashLink as Link } from 'react-router-hash-link';
import {useContext} from "react";
import {Context} from "../context/Context";


export default function LoginPage()
{
	useEffect(()=>
	{
		// Regex for email validation
		let regMail = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/g;
		let regPhone = /^[+]*[(]{0,1}[0-9]{1,4}[)]{0,1}[-\s\./0-9]*$/;
		let emailMessage = document.querySelector(".login__email--message");
		let pwMessage = document.querySelector(".login__pw--message");
		let emailInput  = document.querySelector(".login__identifier");
		let pwInput  = document.querySelector(".login__pw");
		let loginBtn  = document.querySelector(".login__btn--submit");

		function loginSubmit()
		{
			let userID = emailInput.value;
			if((userID.match(regMail) || userID.match(regPhone)))
			{
				emailMessage.classList.remove("show");
				pwMessage.classList.remove("show");
				// ********** User can Login Now **********
				let loginData = {
					ID: userID,
					password: pwInput.value 
				};
				// ***** Use this data to check in the Database *****
				console.log(loginData);
			}
			else
			{
				emailMessage.classList.add("show");
				pwMessage.classList.add("show");
				setTimeout(() =>
				{
					emailMessage.classList.remove("show");
					pwMessage.classList.remove("show");
				}, 5000);
				// ********** User can't Login **********
			}
		}
		loginBtn.addEventListener("click", loginSubmit);

	}, []);
	const userRef = useRef();
	const passwordRef = useRef();

	const {user , dispatch, isFetching } = useContext(Context);



	const handleSubmit= async (e)=>{
	  e.preventDefault();
	  dispatch({type:"LOGIN_START"});
	  try{
		  const res = await axios.post("http://localhost:5000/api/v1/login",{
			email : userRef.current.value,
			password : passwordRef.current.value,
		  })
		  dispatch({type :"LOGIN_SUCCESS", payload:res.data});
		  res.data && window.location.replace('/order_page')

	  }catch(err){ 
		  dispatch({type:"LOGIN_FAILURE"})
	  }
	}
	return (
		<div id='login__id' className="login__page--container">
			<Navbar/>
			<div className="login__page--box flex__col flex__center">
				<div className="login__page--greet medium__paragraph--text">Greeting! Sign in to your Account</div>
				<form className="login__page--form flex__col flex__center" onSubmit={handleSubmit}>

					<div className="login__email--container">
						<input type="text" className='login__identifier' ref = {userRef} placeholder='Email or Phone Number' />
						<div className="login__msg login__email--message">Please enter a valid email</div>
					</div>
					<div className="login__pw--container">
						<input type="password" className="login__pw" placeholder='Password'/>
						<div className="login__msg login__pw--message">Please enter correct password</div>
					</div>
					<submit className="login__btn--submit">LOGIN</submit>
					<div className="login__page--line"></div>
					<div className="login__page--signup--redirect flex__col flex__center medium__paragraph--text">Don't have an account? 
						<span className='signup__redirect'>
							<Link
								to={`/signin_page#signup__id`}
								className="linker nav__item--g"
							>
								<button className="login__signup--btn">SIGNUP</button>
							</Link>
						</span>
					</div>
				</form>
			</div>
			<Footer/>
		</div>
	);
}
