import React from 'react'
import "./style.css"
import "./mobile.css"
import { useEffect } from 'react';
import Navbar from '../common/navbar';
import Footer from '../common/footer';
import Google from "../../img/icons/google.png"
import { HashLink as Link } from 'react-router-hash-link';
import {districts} from "./districts"
import axios from 'axios';
import { useState } from 'react';

export default function SignupPage()
{
	let districtOption = districts.map((district) =>
	{
		return (
			<option value={district}>{district}</option>
		);
	});
	let emailIsValid = false, pwIsValid = false, nameIsValid = false;
	// Create interactable checkbox using "React"
	let agreeBox = React.createElement('input',{type: 'checkbox', defaultChecked: true, className: "signup__subscription", id: "signup__check"});
	useEffect(()=>
	{
		// Regex for email validation
		let regName = /(^[A-Za-z]{2,16})?([ ]{0,1})?([A-Za-z]{2,16})?([ ]{0,1})?([A-Za-z]{2,16})?([ ]{0,1})?([A-Za-z]{2,16})/g;
		let regMail = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/g;
		let regPassword = /^(?=.*\d)(?=.*[a-z])(?=.*[a-zA-Z]).{6,}$/g;
		let emailInput = document.querySelector(".signup__identifier");
		let pwInput = document.querySelector(".signup__pw");
		let nameInput = document.querySelector(".signup__name");
		let phoneInput = document.querySelector(".signup__phone");
		let districtSelector = document.querySelector(".district__selector");
		let streetAddress = document.querySelector(".signup__address");
		let genderSelector = document.querySelector(".gender__selector");
		let subscription = document.querySelector(".signup__subscription");
		let signinRegister = document.querySelector(".signup__btn--submit");
		let emailMessage = document.querySelector(".email__message");
		let pwMessage = document.querySelector(".pw__message");
		let nameMessage = document.querySelector(".fullname__message");

		function handleSignup(e)
		{
			// console.log("Form Changed");
			let elem = e.target;
			if(elem.name === "email")
			{
				if(elem.value.match(regMail))
				{
					console.log("Matched Mail");
					emailMessage.classList.remove("show");
					emailIsValid = true;
				}
				else
				{
					emailMessage.classList.add("show");
					emailIsValid = false;
				}
			}
			else if(elem.name === "password")
			{
				if(elem.value.match(regPassword))
				{
					console.log("Matched Password");
					pwMessage.classList.remove("show");
					pwIsValid = true;
				}
				else
				{
					pwMessage.classList.add("show");
					pwIsValid = false;
				}
			}
			else if(elem.name === "fullname")
			{
				if(elem.value.match(regName))
				{
					console.log("Matched Name");
					nameMessage.classList.remove("show");
					nameIsValid = true;
				}
				else
				{
					nameMessage.classList.add("show");
					nameIsValid = false;
				}
			}

		}


		const  signinSubmit = async(e) =>
		{
			// console.log("Registered!");
			if(emailIsValid && pwIsValid && nameIsValid)
			{

	e.preventDefault();//prevents from reloading and this makes the presence of text in the text-field 
	setError(false);
	try{

	  const res = await  axios.post("http://localhost:5000/api/v1/register",{
		email: emailInput.value,
		password: pwInput.value,
		name: nameInput.value,
		phonenumber: phoneInput.value,
		Address: districtSelector.value,
		Street: streetAddress.value, 
		Gender: genderSelector.value,
		emailSubscribed: subscription.checked ? true : false
	  });
	  res.data && window.location.replace('/login_page')

	}catch(err){
	  setError(true);
	}}

	// 			console.log("Successfully Registered!");
	// 			// Form Value wrap up
	// 			let signUpData = {
	// 				email: emailInput.value,
	// 				password: pwInput.value,
	// 				fullName: nameInput.value,
	// 				phoneNo: phoneInput.value,
	// 				district: districtSelector.value,
	// 				streetAddress: streetAddress.value, 
	// 				gender: genderSelector.value,
	// 				emailSubscribed: subscription.checked ? true : false
	// 			};
	// 			// ********** Use this data to store in database **********
	// 			console.log(signUpData);
	// 		}
			else
			{
				console.log("Registration failed. Please try again.");

			}
		};
		emailInput.addEventListener("keyup", handleSignup);
		pwInput.addEventListener("keyup", handleSignup);
		nameInput.addEventListener("keyup", handleSignup);
		signinRegister.addEventListener("click", signinSubmit);

	}, []);

	const [name, setName] = useState("");
const [email, setEmail] = useState("");
const [password, setPassword] = useState("");
const [phonenumber, setPhonenumber] = useState("");
const [Address, setAddress] = useState("");
const [Street, setStreet] = useState("");
const [Gender, setGender] = useState("");
const [error, setError] = useState(false);
// 	const handleSubmit = async(e)=>{
//   e.preventDefault();//prevents from reloading and this makes the presence of text in the text-field 
//   setError(false);
//   try{

// 	const res = await  axios.post("http://localhost:5000/api/v1/register",{
// 	  name ,
// 	  email,
// 	  password,
// 	  phonenumber,
// 	  Address,
// 	  Street,
// 	  Gender,

// 	});
// 	res.data && window.location.replace('/login_page')

//   }catch(err){
// 	setError(true);
//   }}

	return (
		<div id='signup__id' className="signup__page--container">
			<Navbar/>
			<div className="signup__page--box flex__col flex__center">
				<div className="signup__page--greet medium__paragrapg--text">Create your new Account</div>
				<div className="signup__page--signup--redirect medium__paragrapg--text">Already have an account? 
					<span className='signup__redirect'>
						<Link
							to={`/login_page#login__id`}
							className="linker nav__item--g"
						>
							&nbsp; Login &nbsp;
						</Link>
					</span>
					from here
				</div>
					<div className="signup__page flex__col flex__center">
				<form action="/login_page" onSubmit={signinSubmit}>
					<div className="email__container flex__col">
						<input name='email' type="email" className='signup__identifier' placeholder='Email Address *' onChange={(e)=>setEmail(e.target.value)} required/>
						<div className="signin__msg email__message">Please enter a valid email</div>
					</div>
					<div className="password__fullname--field flex__row">
						<div className="pw__container flex__col">
							<input name='password' type="password" className="signup__pw" placeholder='Password *' onChange={(e)=>setPassword(e.target.value)} required/>
							<div className="signin__msg pw__message">Password must contain at least 6 characters</div>
						</div>
						<div className="fullname__container flex__col">
							<input name='fullname' type="text" className="signup__name" placeholder='Full Name *' onChange={(e)=>setName(e.target.value)} required/>
							<div className="signin__msg fullname__message">Name must've at least 2 characters</div>
						</div>
					</div>
					<div className="signup__phone--address flex__row">
						<input type="number" className="signup__phone" onChange={(e)=>setPhonenumber(e.target.value)} placeholder='Phone No. *' required/>
						<select name="districts" id='districts' className='district__selector' onChange={(e)=>setAddress(e.target.value)} required>
							<option value="none" selected disabled hidden className='district__select--placeholder'>Select a District *</option>	
							{districtOption}
						</select>
					</div>
					<div className="street__gender--field flex__row">
						<input type="text" className="signup__address" onChange={(e)=>setStreet(e.target.value)} placeholder='Street Address'/>
						<select name="gender" id='gender' className='gender__selector' onChange={(e)=>setGender(e.target.value)} required>
							<option value="none" selected disabled hidden className='gender__select--placeholder'>Gender</option>	
							<option value="Male" className='gender__select--option'>Male</option>	
							<option value="Female" className='gender__select--option'>Female</option>	
						</select>
					</div>
					<div className="signup__agreement flex__row flex__center footer__paragraph--text">
